inSequence.script:
var gender = mc.getProperty('gender');
var response_gender;
if (gender.toUpperCase() === 'MALE') {
  response_gender = 'M';
mc.setProperty("response_gender", response_gender)
} else if (gender.toUpperCase() === 'FEMALE') {
  response_gender = 'F';
mc.setProperty("response_gender", response_gender)
} else if (gender.toUpperCase() === 'F' || gender.toUpperCase() === 'M') {
  response_gender = gender;
mc.setProperty("response_gender", response_gender)
}
